import React from 'react'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs'
import 'react-tabs/style/react-tabs.css';
import BarInfo from './barInfo'
import { GoogleMap, withScriptjs, withGoogleMap, Marker} from 'react-google-maps';

function Map(props) {
    return (
        <GoogleMap
            defaultZoom={10}
            defaultCenter={{ lat: props.currentCompany.latitude, lng: props.currentCompany.longitude }}
        ></GoogleMap>
    )
}

const WrappedMap = withScriptjs(withGoogleMap(Map))

class CompanyNetwork extends React.Component {
    render() {
        let url = "https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key="
        return (
            <div className="mt-5">
                <h2 className="text-center">{this.props.currentCompany.company_name}</h2>
                <Tabs>
                    <TabList>
                        <Tab>Info</Tab>
                        <Tab>Adresse</Tab>
                    </TabList>
                    <TabPanel>
                        <div className="row">
                            <div className="col-sm-6">
                                <BarInfo name="Nature" value={this.props.currentCompany.nature} />
                            </div>
                            <div className="col-sm-6">
                                <BarInfo name="Date de création" value={this.props.currentCompany.creation_year} />
                            </div>
                        </div>
                        <BarInfo name="Statut juridique" value={this.props.currentCompany.juridic_form} />
                        <BarInfo name="Activités" value={this.props.currentCompany.activities} />


                    </TabPanel>
                    <TabPanel>
                        <BarInfo name="Ville" value={this.props.currentCompany.ville} />
                        <BarInfo name="Adresse" value={this.props.currentCompany.address} />

                        <div className="row">
                            <div className="col-sm-6">
                                <BarInfo name="Longitude" value={this.props.currentCompany.longitude} />
                            </div>
                            <div className="col-sm-6">
                                <BarInfo name="Latitude" value={this.props.currentCompany.latitude} />
                            </div>
                        </div>

                    </TabPanel>
                </Tabs>
                <div style={{ width: '100vw', height: '100vh' }}>
                    <WrappedMap
                        googleMapURL={url}
                        loadingElement={<div style={{ height: '100%' }} />}
                        containerElement={<div style={{ height: `400px` }} />}
                        mapElement={<div style={{ height: `100%` }} />}
                    />
                </div>
            </div>
        )
    }
}

export default CompanyNetwork
