import React from 'react';
import './App.css';
import axios from 'axios'
import CompanyNetwork from './components/CompanyNetwork'
//import CompanyGraph from './components/companyGraph'
import MyNav from './components/nav'



class App extends React.Component {
  constructor(){
    super()
    this.state = {
      currentCompany:{},
      partenaire:{}
    }
  }
  componentDidMount(){
    const api = axios.create({
      baseURL: 'http://localhost:9200/proto/_search?q=company_name:"Entreprise Services CDG"'
    })
    api.get('')
      .then(async(res) => {
        let var1 = res.data.hits.hits[0]._source
        let stringSearch = 'http://localhost:9200/proto/_search?q=id:'
        +res.data.hits.hits[0]._source.Partenaire__002+'%20OR'
        api.get('http://localhost:9200/proto/_search?q=id:'+res.data.hits.hits[0]._source.Partenaire__002)
        .then(async(ress) => {
          let var2 = ress.data.hits.hits[0]._source
          this.setState({
            currentCompany:var1,
            partenaire:var2
          })
        })
    });
  }

  render() {
    //console.log(this.state.partenaire)
    return (
      <div>
        <MyNav />
        <div className="row mt-5">
          <div className="col-sm-4 ml-5">
            <CompanyNetwork currentCompany={this.state.currentCompany}/>
          </div>
          <div className="col-sm-7 mr-5">
          <CompanyNetwork currentCompany={this.state.partenaire}/>
          </div>
        </div>
      </div>
    )

  }
}

export default App;
