import React from 'react'
import axios from 'axios'

class CompanyGraph extends React.Component{
    constructor(){
        super()
        this.state = {
          others:[]
        }
      }
    componentDidMount(){
        const api = axios.create({
          baseURL: `http://localhost:9200/proto/_search?q=id:${this.props.currentCompany}`
        })
        api.get('')
          .then(async(res) => {
            console.log(res.data.hits.hits[0]._source.company_name)
            this.setState({others:res.data.hits.hits[0]._source})
        });
    }
    render(){
        return(
            <div></div>
        )
    }
}

export default CompanyGraph
