import React from 'react'
import { CompaniesData, renderCompanyName } from './companies-data';
import Autocomplete from 'react-autocomplete';

class Conditional extends React.Component {
    constructor() {
        super()
        this.state = {
            val: ""
        }
    }
    render() {
        return (
            <div>
                <h1 id="titre" className="text-center mr-5 ml-5">
                    <i className="fa fa-search" aria-hidden="true"></i>
                    <strong> Search Engine </strong>
                </h1>
                <div id="cover">
                    <form method="get" action="">
                        <div className="tb">
                            <div className="autocomplete-wrapper">
                                <Autocomplete
                                    value={this.state.val}
                                    items={CompaniesData()}
                                    getItemValue={item => item.company_name}
                                    shouldItemRender={renderCompanyName}
                                    renderMenu={item => (
                                        <div className="dropdown">
                                            {item}
                                        </div>
                                    )}
                                    renderItem={(item, isHighlighted) =>
                                        <div className={`item ${isHighlighted ? 'selected-item' : ''}`}>
                                            {item.company_name}
                                        </div>
                                    }
                                    onChange={(event, val) => this.setState({ val })}
                                    onSelect={val => this.setState({ val })}
                                />
                            </div>




                            <div className="td"><input type="text" onChange={this.props.handleChange} placeholder="Entrez l'entreprise recherchée .." required /></div>
                            <div className="td" id="s-cover">
                                <button type="submit" onClick={this.props.handleClick}>
                                    <div id="s-circle"></div>
                                    <span></span>
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
                <div className="backImage"></div>
                <div className="backImage2"></div>
                <p id="copyrights" className="text-center">&copy; 2020 Search Engine</p>

            </div>
        )
    }

}


export default Conditional
