import React from 'react'
import Conditional from './conditional'
import App from './App';
import './style.css'



class Formz extends React.Component {
    constructor() {
        super()
        this.state = {
            isLogged: false,
            companyName: ""
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleNew = this.handleNew.bind(this)
    }

    handleClick() {
        this.setState(prevState => {
            return {
                isLogged: !prevState.isLogged,
                companyName: prevState.companyName
            }
        })
    }
    handleChange(event) {
        this.setState({
            isLogged: false,
            companyName: event.target.value

        })
    }
    handleNew(event) {
        this.setState({
            isLogged: false,
            companyName: event.target.value
        })
        setTimeout(function(){
            this.setState({
                isLogged: true,
                companyName: this.state.companyName
            })
            console.log(this.state.companyName)
       }.bind(this),200);
    }

    render() {
        if (this.state.isLogged) {
            return (
                <App companyName={this.state.companyName} handleClick={this.handleClick} handleNew={this.handleNew}  />
            )
        }
        return (
            <Conditional handleChange={this.handleChange} handleClick={this.handleClick} />
        )
    }
}

export default Formz
