import React from 'react';
import './App.css';
import axios from 'axios'
import CompanyNetwork from './components/CompanyNetwork'
import { Network, Node, Edge } from 'react-vis-network'
import MyNav from './components/nav'
import names from './names'



class App extends React.Component {
  constructor() {
    super()
    this.state = {
      networkComponent: React.createRef(),
      currentCompany: {},
      relations: [],
      partners: false,
      headquarter: false,
      filiales: false,
      clients: false,
      fournisseurs: false
    }
  }
  componentDidMount() {
    this.state.networkComponent.current.network.on("click", event => {
      //console.log("clicked", names.includes(event.nodes[0]))
      if (names.includes(event.nodes[0])) {
        this.props.handleNew(event.nodes[0])
      }
    });
    const api = axios.create({
      baseURL: 'http://localhost:9200/proto/_search?q=company_name:"' + this.props.companyName + '"'
    })
    api.get('')
      .then(async (res) => {
        let var1 = res.data.hits.hits[0]._source
        let stringSearch = 'http://localhost:9200/proto/_search?q=id:'
          + var1.Partenaire__002 + '%20OR%20id:'
          + var1.Partenaire__001 + '%20OR%20id:'
          + var1["Clients__-"] + '%20OR%20id:'
          + var1["filiales__-"] + '%20OR%20id:'
          + var1["fournisseur__-"] + '%20OR%20id:'
          + var1.hearquarter
        api.get(stringSearch)
          .then(async (ress) => {
            let relations = []
            let partnerz = false, clients = false, fournisseurs = false, heads = false, filiales = false
            for (let i = 0; i < ress.data.hits.hits.length; i++) {
              relations.push(ress.data.hits.hits[i]._source)
              if (var1.Partenaire__001 == ress.data.hits.hits[i]._source.id || var1.Partenaire__002 == ress.data.hits.hits[i]._source.id) {
                partnerz = true
              }
              if (var1["Clients__-"] == ress.data.hits.hits[i]._source.id) {
                clients = true
              }
              if (var1["fournisseur__-"] == ress.data.hits.hits[i]._source.id) {
                fournisseurs = true
              }
              if (var1.hearquarter == ress.data.hits.hits[i]._source.id) {
                heads = true
              }
              if (var1["filiales__-"] == ress.data.hits.hits[i]._source.id) {
                filiales = true
              }
            }
            //console.log(var1, relations)
            this.state = { currentCompany: {}, relations: [] }
            this.setState({
              currentCompany: var1,
              relations: relations,
              partners: partnerz,
              headquarter: heads,
              filiales: filiales,
              clients: clients,
              fournisseurs: fournisseurs
            })
          })
      });
  }

  render() {
    const partnersNode = this.state.relations.map(partner => {
      let colorNode
      let shape = "box"
      let font = { color: "white", size: 20 }
      if (partner.id == this.state.currentCompany.hearquarter || partner.id == this.state.currentCompany["filiales__-"]) {
        colorNode = "#888888"
      } else {
        if (partner.id == this.state.currentCompany.Partenaire__002 || partner.id == this.state.currentCompany.Partenaire__001) {
          colorNode = "#AC3B61"
        } else {
          if (partner.id == this.state.currentCompany["fournisseur__-"]) {
            colorNode = "#123C69"
          } else {
            if (partner.id == this.state.currentCompany["Clients__-"]) {
              colorNode = "#FFAE42"
            } else {
              colorNode = "#000000"
            }
          }
        }
      }
      return <Node id={partner.company_name} label={partner.company_name} color={colorNode} shape={shape} font={font} />
    })


    const partnersEdges = this.state.relations.map(partner => {
      if (partner.id == this.state.currentCompany.hearquarter) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="200" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany.Partenaire__002 || partner.id == this.state.currentCompany.Partenaire__001) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="100" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany["fournisseur__-"]) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="300" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany["Clients__-"]) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="400" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany["filiales__-"]) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="500" to={partner.company_name} />
      }
      return <Edge />
    })


    return (
      <div>
        <MyNav handleClick={this.handleClick} />
        <div className="row mt-5">
          <div className="col-sm-4 ml-5">
            <CompanyNetwork currentCompany={this.state.currentCompany} />
          </div>
          <div className="col-sm-7">
            <div className="zmer">
              <Network ref={this.state.networkComponent}>
                <Node id="1000" label={this.state.currentCompany.company_name} color="#000000" fixed={{ x: true, y: true }} font={{ color: "white", size: 25 }} />
                <Node hidden={!this.state.partners} id="100" label="Partenaires" x="100" y="100" color="#AC3B61" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#AC3B61" }} />
                <Node hidden={!this.state.headquarter} id="200" label="Headquarter" color="#888888" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#888888" }} />
                <Node hidden={!this.state.fournisseurs} id="300" label="Fournisseurs" color="#123C69" x="0" y="100" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#123C69" }} />
                <Node hidden={!this.state.clients} id="400" label="Clients" color="#FFAE42" size="15" x="100" y="0" fixed={{ x: true, y: true }} shape="dot" font={{ color: "#FFAE42" }} />
                <Node hidden={!this.state.filiales} id="500" label="Filiales" color="#888888" size="15" y="0"fixed={{ x: true, y: true }} shape="dot" font={{ color: "#888888" }} />
                {this.state.partners?<Edge id="100" from="1000" to="100" />:<Edge />}
                {this.state.headquarter?<Edge id="200" from="1000" to="200" />:<Edge />}
                {this.state.fournisseurs?<Edge id="300" from="1000" to="300" />:<Edge />}
                {this.state.clients?<Edge id="400" from="1000" to="400" />:<Edge />}
                {this.state.filiales?<Edge id="500" from="1000" to="500" />:<Edge />}
                {partnersEdges}
                {partnersNode}
              </Network>
            </div>
          </div>
        </div>
      </div>
    )

  }
}

export default App;
