import React from 'react';
import axios from 'axios'
import CompanyInfo from './CompanyInfo'
import { Network, Node, Edge } from 'react-vis-network'
import MyNav from './nav'
import names from './names'
import { nodeColorsConfig } from './usefulFunctions'


class CompanyPage extends React.Component {
  constructor() {
    super()
    this.state = {
      networkComponent: React.createRef(),
      //  currentCompany est un objet JSON qui contient tous les infos de l'entreprise actuelle
      currentCompany: {},
      //  relations est un array contenant tous les partenaires, filiales, clients et fournisseurs de l'entreprise actuelle
      relations: [],
      //  Les booleans suivants servent à gérer l'affichage des noeuds qui précisent le type de relation entre 
      //  l'entreprise actuelle et les autres. Par exemple, si l'entreprise actuelle possède une ou plusieurs filiales
      //  la valeur du boolean filiales sera changé à true 
      partners: false,
      headquarter: false,
      filiales: false,
      clients: false,
      fournisseurs: false
    }
  }
  componentDidMount() {
    this.state.networkComponent.current.network.on("click", event => {
      if (names.includes(event.nodes[0])) {
        this.props.handleNew(event.nodes[0])
      }
    });


    const api = axios.create({
      baseURL: 'http://localhost:9200/proto/_search?q=company_name:"' + this.props.companyName + '"'
    })
    api.get('')
      .then(async (res) => {
        let currentCompany = res.data.hits.hits[0]._source

        let stringSearch = 'http://localhost:9200/proto/_search?q=id:'
          + currentCompany.Partenaire__002 + '%20OR%20id:'
          + currentCompany.Partenaire__001 + '%20OR%20id:'
          + currentCompany["Clients__-"] + '%20OR%20id:'
          + currentCompany["filiales__-"] + '%20OR%20id:'
          + currentCompany["fournisseur__-"] + '%20OR%20id:'
          + currentCompany.hearquarter

        api.get(stringSearch)
          .then(async (result) => {

            let relationsResult = result.data.hits.hits
            let relations = [], partners = false, clients = false, fournisseurs = false, headquarter = false, filiales = false

            for (let i = 0; i < relationsResult.length; i++) {
              relations.push(relationsResult[i]._source)
              if (currentCompany.Partenaire__001 == relationsResult[i]._source.id || currentCompany.Partenaire__002 == relationsResult[i]._source.id) {
                partners = true
              }
              if (currentCompany["Clients__-"] == relationsResult[i]._source.id) { clients = true }
              if (currentCompany["fournisseur__-"] == relationsResult[i]._source.id) { fournisseurs = true }
              if (currentCompany.hearquarter == relationsResult[i]._source.id) { headquarter = true }
              if (currentCompany["filiales__-"] == relationsResult[i]._source.id) { filiales = true }
            }

            this.setState({
              currentCompany,
              relations,
              partners,
              headquarter,
              filiales,
              clients,
              fournisseurs
            })
          })
      });
  }

  render() {

    //  La constantes suivante contients les noeuds du réseau
    // La fonction nodeColorConfig se charge de la configuration
    // des couleurs selon le type de la relation
    const partnersNode = this.state.relations.map(partner => {
      let config = nodeColorsConfig(this.state.currentCompany, partner)
      return <Node
        id={partner.company_name}
        x={config.x}
        y={config.y}
        label={partner.company_name}
        color={config.colorNode}
        shape={config.shape}
        font={config.font}
      />
    })


    const partnersEdges = this.state.relations.map(partner => {
      if (partner.id == this.state.currentCompany.hearquarter) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="200" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany.Partenaire__002 || partner.id == this.state.currentCompany.Partenaire__001) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="100" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany["fournisseur__-"]) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="300" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany["Clients__-"]) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="400" to={partner.company_name} />
      }
      if (partner.id == this.state.currentCompany["filiales__-"]) {
        return <Edge id={this.state.currentCompany.id + partner.id} from="500" to={partner.company_name} />
      }
      return <Edge />
    })


    return (
      <div>
        <MyNav handleClick={this.handleClick} />
        <div className="row mt-5">
          <div className="col-sm-4 ml-5">
            <CompanyInfo currentCompany={this.state.currentCompany} />
          </div>
          <div className="col-sm-7">
            <div className="zmer">
              <Network ref={this.state.networkComponent}>
                <Node id="1000" label={this.state.currentCompany.company_name} color="#000000" x="0" y="100" fixed={{ x: true, y: true }} font={{ color: "white", size: 25 }} />
                {this.state.partners ? <Node id="100" label="Partenaires" color="#AC3B61" x="200" y="200" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#AC3B61" }} /> : <Edge />}
                {this.state.headquarter ? <Node id="200" label="Headquarter" color="#888888" x="200" y="0" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#888888" }} /> : <Edge />}
                {this.state.fournisseurs ? <Node id="300" label="Fournisseurs" color="#123C69" x="200" y="100" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#123C69" }} /> : <Edge />}
                {this.state.clients ? <Node id="400" label="Clients" color="#FFAE42" size="15" x="200" y="0" fixed={{ x: true, y: true }} shape="dot" font={{ color: "#FFAE42" }} /> : <Edge />}
                {this.state.filiales ? <Node id="500" label="Filiales" color="#888888" size="15" fixed={{ x: true, y: true }} shape="dot" font={{ color: "#888888" }} /> : <Edge />}
                {this.state.partners ? <Edge id="100" from="1000" smooth={{ enabled: true }} to="100" /> : <Edge />}
                {this.state.headquarter ? <Edge id="200" from="1000" smooth={{ enabled: true }} to="200" /> : <Edge />}
                {this.state.fournisseurs ? <Edge id="300" from="1000" smooth={{ enabled: true }} to="300" /> : <Edge />}
                {this.state.clients ? <Edge id="400" from="1000" smooth={{ enabled: true }} to="400" /> : <Edge />}
                {this.state.filiales ? <Edge id="500" from="1000" smooth={{ enabled: true }} to="500" /> : <Edge />}
                {partnersEdges}
                {partnersNode}
              </Network>
            </div>
          </div>
        </div>
      </div>
    )

  }
}

export default CompanyPage;
