import React from 'react'
import SearchBarPage from './SearchBarPage'
import CompanyPage from './CompanyPage';
import names from './names'



class SearchEngineApp extends React.Component {
    constructor() {
        super()
        this.state = {
            isLogged: false,
            companyName: ""
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleNew = this.handleNew.bind(this)
    }

    handleClick() {
        this.setState(prevState => {
            return {
                isLogged: !prevState.isLogged,
                companyName: prevState.companyName
            }
        })
    }
    handleChange(event) {
        if (names.includes(event.target.value)){
            console.log('the name is in there')
        }
        /*this.setState({
            isLogged: false,
            companyName: event.target.value
        })*/
    }
    handleNew(event) {
        this.setState({
            isLogged: false,
            companyName: event
        })
        setTimeout(function () {
            this.setState({
                isLogged: true,
                companyName: this.state.companyName
            })
        }.bind(this), 5);
    }

    render() {
        return (
            this.state.isLogged ?
                <CompanyPage companyName={this.state.companyName} handleClick={this.handleClick} handleNew={this.handleNew} />
                : <SearchBarPage handleChange={this.handleChange} handleClick={this.handleClick} />
        )
    }
}

export default SearchEngineApp
