import React from 'react';
import './App.css';
import axios from 'axios'
import CompanyNetwork from './components/CompanyNetwork'
import { Network, Node, Edge } from 'react-vis-network'
//import CompanyGraph from './components/companyGraph'
import MyNav from './components/nav'



class App extends React.Component {
  constructor() {
    super()
    this.state = {
      currentCompany: {},
      relations: []
    }
  }
  componentDidMount() {
    const api = axios.create({
      baseURL: 'http://localhost:9200/proto/_search?q=company_name:"Entreprise Services CDG"'
    })
    api.get('')
      .then(async (res) => {
        let var1 = res.data.hits.hits[0]._source
        let stringSearch = 'http://localhost:9200/proto/_search?q=id:'
          + var1.Partenaire__002 + '%20OR%20id:'
          + var1.Partenaire__001 + '%20OR%20id:'
          + var1["fournisseur__-"] + '%20OR%20id:'
          + var1.hearquarter
        api.get(stringSearch)
          .then(async (ress) => {
            let relations = []
            for (let i = 0; i < ress.data.hits.hits.length; i++) {
              relations.push(ress.data.hits.hits[i]._source)
            }
            this.setState({
              currentCompany: var1,
              relations: relations
            })
          })
      });
  }

  render() {
    const thepartners = this.state.relations.map(partner => {
      return <CompanyNetwork currentCompany={partner} />
    })
    return (
      <div>
        <MyNav />
        <div className="row mt-5">
          <div className="col-sm-4 ml-5">
            <CompanyNetwork currentCompany={this.state.currentCompany} />
          </div>
          <div className="col-sm-7 mr-5">
            <Network>
              <Node id="0" label="Partenaires" color="#000000" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#AC3B61" }} />
              <Node id="100" label="Partenaires" color="#AC3B61" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#AC3B61" }} />
              <Node id="200" label="Headquarter" color="#888888" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#888888" }} />
              <Node id="300" label="Fournisseurs" color="#123C69" fixed={{ x: true, y: true }} size="15" shape="dot" font={{ color: "#123C69" }} />
              <Edge id="100" from="0" to="100" />
              <Edge id="200" from="0" to="200" />
              <Edge id="300" from="0" to="300" />
              {ComapanyNodes}
              {ComapanyEdges}
            </Network>
          </div>
        </div>
      </div>
    )

  }
}

export default App;
